import React from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/AdminPanel.css';
import { FiPackage, FiSettings, FiLogOut } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';

const AdminPanel = () => {
  const { allOrders = [], updateOrderStatus, isAdmin, user, logout } = useCart(); // Добавлено значение по умолчанию
  const navigate = useNavigate();

  // Функция выхода
  const handleLogout = () => {
    logout();
    navigate('/');
  };

  if (!isAdmin) {
    return (
      <div className="admin-layout">
        <Header />
        <main className="admin-panel">
          <div className="admin-denied">
            <h1>Доступ запрещен</h1>
            <p>Требуются права администратора</p>
          </div>
        </main>
        <Footer />
      </div>
    );
  }

  const statusOptions = [
    { value: 'pending', label: 'Ожидает' },
    { value: 'processing', label: 'В обработке' },
    { value: 'shipped', label: 'Отправлен' },
    { value: 'delivered', label: 'Доставлен' },
    { value: 'cancelled', label: 'Отменен' }
  ];

  // Проверка и форматирование даты заказа
  const formatOrderDate = (date) => {
    if (!date) return 'Дата не указана';
    try {
      return new Date(date).toLocaleString();
    } catch {
      return 'Некорректная дата';
    }
  };

  return (
    <div className="admin-layout">
      <Header />
      <main className="admin-panel">
        <div className="admin-header">
          <h1>
            <FiSettings size={28} />
            Панель администратора
          </h1>
          <div className="admin-info">
            <p>Вы вошли как: <strong>{user?.name || 'Администратор'}</strong></p>
            <p>Email: <strong>{user?.email || 'admin@example.com'}</strong></p>
          </div>
          <button onClick={handleLogout} className="admin-logout-btn">
            <FiLogOut size={18} /> Выйти
          </button>
        </div>

        <div className="admin-sections">
          <section className="orders-section">
            <h2><FiPackage /> Управление заказами</h2>
            <div className="orders-grid">
              {!allOrders || allOrders.length === 0 ? (
                <p className="no-orders">Нет заказов для отображения</p>
              ) : (
                allOrders.map(order => (
                  <div key={order.id} className="order-card">
                    <div className="order-header">
                      <h3>Заказ #{order.id?.slice(0, 8) || 'N/A'}</h3>
                      <span className={`status-badge ${order.status || 'pending'}`}>
                        {statusOptions.find(s => s.value === (order.status || 'pending'))?.label}
                      </span>
                    </div>
                    
                    <div className="order-details">
                      <p><strong>Дата:</strong> {formatOrderDate(order.date)}</p>
                      <p><strong>Сумма:</strong> {(order.total || 0).toLocaleString()} ₽</p>
                      <p><strong>Товаров:</strong> {order.items?.length || 0}</p>
                    </div>

                    <div className="order-controls">
                      <select
                        value={order.status || 'pending'}
                        onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                        className="status-select"
                      >
                        {statusOptions.map(option => (
                          <option key={option.value} value={option.value}>
                            {option.label}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="order-items">
                      {order.items?.map(item => (
                        <div key={item.cartId} className="order-item">
                          <img src={item.image || '/placeholder-product.png'} alt={item.name} width="40" />
                          <span>{item.name || 'Неизвестный товар'}</span>
                          <span>{(item.price || 0).toLocaleString()} ₽</span>
                        </div>
                      )) || <p>Нет информации о товарах</p>}
                    </div>
                  </div>
                ))
              )}
            </div>
          </section>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default AdminPanel;
