import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut } from 'react-icons/fi';
import { Link } from 'react-router-dom';

const Profile = () => {
  const { user, fetchCurrentUser, logout } = useCart();
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const loadUser = async () => {
      await fetchCurrentUser();
      setLoading(false);
    };

    loadUser();
  }, [fetchCurrentUser]);

  if (loading) {
    return <div>Загрузка...</div>;
  }

  if (!user) {
    return (
      <div>
        <Header />
        <main className="profile-page">
          <h2>Войдите, чтобы посмотреть профиль</h2>
          <Link to="/auth">Перейти к авторизации</Link>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">Профиль</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user?.name || 'Гость'}</h2>
            <p><strong>Email:</strong> {user?.email || 'Не указан'}</p>
            <p><strong>ID:</strong> {user?.id || 'Нет данных'}</p>
            <p><strong>Роль:</strong> {user?.isAdmin ? 'Администратор' : 'Пользователь'}</p>

            <button onClick={logout}>
              <FiLogOut /> Выйти
            </button>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Profile;
