import { createContext, useContext, useState, useEffect, useRef } from 'react';

const CartContext = createContext();

export function CartProvider({ children }) {
  const [cartItems, setCartItems] = useState([]);
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  const safeSetState = (setter, value) => {
    if (isMounted.current) {
      setter(value);
    }
  };

  const fetchCart = async (userId) => {
    safeSetState(setIsLoading, true);
    safeSetState(setError, null);
    try {
      const response = await fetch(`http://localhost:5000/api/cart/${userId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (!response.ok) {
        throw new Error('Ошибка загрузки корзины');
      }
      
      const data = await response.json();
      safeSetState(setCartItems, data.items || []);
    } catch (error) {
      safeSetState(setError, error.message);
      console.error('Ошибка загрузки корзины:', error);
    } finally {
      safeSetState(setIsLoading, false);
    }
  };

  const saveCart = async (items) => {
    if (!user) return;
    
    safeSetState(setIsLoading, true);
    safeSetState(setError, null);
    try {
      const response = await fetch(`http://localhost:5000/api/cart/${user.id}`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ items }),
      });
      
      if (!response.ok) {
        throw new Error('Ошибка сохранения корзины');
      }
    } catch (error) {
      safeSetState(setError, error.message);
      console.error('Ошибка сохранения корзины:', error);
    } finally {
      safeSetState(setIsLoading, false);
    }
  };

  // Добавление товара
  const addToCart = async (product) => {
    if (!user) return;
    
    const existingItem = cartItems.find(item => item.productId === product.id);
    let updatedCart;
    
    if (existingItem) {
      updatedCart = cartItems.map(item => 
        item.productId === product.id 
          ? { ...item, quantity: item.quantity + 1 } 
          : item
      );
    } else {
      updatedCart = [
        ...cartItems,
        {
          ...product,
          cartId: `${product.id}_${Date.now()}`,
          quantity: 1
        }
      ];
    }
    
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Удаление товара
  const removeFromCart = async (cartId) => {
    if (!user) return;
    
    const updatedCart = cartItems.filter(item => item.cartId !== cartId);
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Обновление количества
  const updateQuantity = async (cartId, newQuantity) => {
    if (!user) return;
    
    const updatedCart = cartItems.map(item => 
      item.cartId === cartId 
        ? { ...item, quantity: Math.max(1, newQuantity) } 
        : item
    );
    
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Вход
  const login = (userData) => {
    safeSetState(setUser, userData);
    safeSetState(setIsAuthenticated, true);
    fetchCart(userData.id);
  };

  // Выход
  const logout = () => {
    safeSetState(setUser, null);
    safeSetState(setIsAuthenticated, false);
    safeSetState(setCartItems, []);
  };

  return (
    <CartContext.Provider
      value={{
        cartItems,
        cartCount: cartItems.reduce((sum, item) => sum + item.quantity, 0),
        addToCart,
        removeFromCart,
        updateQuantity,
        user,
        isAuthenticated,
        isLoading,
        error,
        login,
        logout
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
