import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/AdminPanel.css';
import { FiPackage, FiSettings, FiLogOut, FiAlertTriangle } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminPanel = () => {
  // Получаем данные из контекста корзины (пользователь, выход, проверка админа)
  const { user, logout, isAdmin } = useCart();
  
  // Состояние для хранения списка заказов
  const [allOrders, setAllOrders] = useState([]);
  
  // Состояние для отображения загрузки
  const [isLoading, setIsLoading] = useState(true);
  
  // Хук для навигации между страницами
  const navigate = useNavigate();

  // Варианты статусов заказов с переводами
  const statusOptions = [
    { value: 'pending', label: 'Ожидает' },
    { value: 'processing', label: 'В обработке' },
    { value: 'shipped', label: 'Отправлен' },
    { value: 'delivered', label: 'Доставлен' },
    { value: 'cancelled', label: 'Отменен' }
  ];

  // Тестовые данные заказов (в реальном приложении будут приходить с сервера)
  const mockOrders = [
    {
      id: 'order-1',
      date: new Date(),
      status: 'pending',
      total: 4500,
      items: [
        { id: 'item-1', name: 'Товар 1', price: 1500, quantity: 1 },
        { id: 'item-2', name: 'Товар 2', price: 3000, quantity: 1 }
      ]
    },
    {
      id: 'order-2',
      date: new Date(Date.now() - 86400000), // Вчерашняя дата
      status: 'processing',
      total: 7500,
      items: [
        { id: 'item-3', name: 'Товар 3', price: 7500, quantity: 1 }
      ]
    }
  ];

  // Эффект для загрузки заказов при открытии страницы
  useEffect(() => {
    // Имитируем загрузку данных с сервера
    const timer = setTimeout(() => {
      setAllOrders(mockOrders);
      setIsLoading(false);
    }, 500);

    // Очистка таймера при размонтировании компонента
    return () => clearTimeout(timer);
  }, []);

  // Функция выхода из системы
  const handleLogout = () => {
    logout(); // Вызываем метод logout из контекста
    toast.info('Вы вышли из системы', {
      position: "top-center",
      autoClose: 3000,
    });
    navigate('/'); // Перенаправляем на главную страницу
  };

  // Функция обновления статуса заказа
  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      // Обновляем статус в локальном состоянии
      setAllOrders(prevOrders => 
        prevOrders.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
      
      // Показываем уведомление об успехе
      toast.success(`Статус заказа #${orderId} изменен на "${newStatus}"`, {
        position: "top-center",
        autoClose: 3000,
      });
    } catch (error) {
      // Показываем ошибку, если что-то пошло не так
      toast.error('Ошибка при обновлении статуса', {
        position: "top-center",
        autoClose: 5000,
      });
    }
  };

  // Если пользователь не админ - показываем сообщение о запрете доступа
  if (!isAdmin()) {
    return (
      <div className="admin-layout">
        <Header />
        <main className="admin-panel">
          <div className="admin-denied">
            <FiAlertTriangle size={48} className="denied-icon" />
            <h1>Доступ запрещен</h1>
            <p>Требуются права администратора</p>
            <button onClick={() => navigate('/')} className="return-btn">
              Вернуться на главную
            </button>
          </div>
        </main>
        <Footer />
      </div>
    );
  }

  // Основной интерфейс админ-панели
  return (
    <div className="admin-layout">
      <Header />
      <main className="admin-panel">
        {/* Шапка админ-панели с информацией о пользователе */}
        <div className="admin-header">
          <h1>
            <FiSettings size={28} />
            Панель администратора
          </h1>
          <div className="admin-info">
            <p>Вы вошли как: <strong>{user?.name || 'Администратор'}</strong></p>
            <p>Email: <strong>{user?.email || 'admin@example.com'}</strong></p>
          </div>
          <button onClick={handleLogout} className="admin-logout-btn">
            <FiLogOut size={18} /> Выйти
          </button>
        </div>

        {/* Основной контент - управление заказами */}
        <div className="admin-sections">
          <section className="orders-section">
            <h2><FiPackage /> Управление заказами</h2>
            
            {/* Индикатор загрузки */}
            {isLoading ? (
              <div className="loading-indicator">Загрузка заказов...</div>
            ) : (
              <div className="orders-grid">
                {/* Если заказов нет */}
                {allOrders.length === 0 ? (
                  <div className="no-orders">
                    <p>Нет заказов для отображения</p>
                  </div>
                ) : (
                  /* Список всех заказов */
                  allOrders.map(order => (
                    <div key={order.id} className="order-card">
                      {/* Шапка карточки заказа */}
                      <div className="order-header">
                        <h3>Заказ #{order.id?.slice(0, 8) || 'N/A'}</h3>
                        {/* Бейдж со статусом заказа */}
                        <span className={`status-badge ${order.status || 'pending'}`}>
                          {statusOptions.find(s => s.value === (order.status || 'pending'))?.label}
                        </span>
                      </div>
                      
                      {/* Детали заказа */}
                      <div className="order-details">
                        <p><strong>Дата:</strong> {new Date(order.date).toLocaleString()}</p>
                        <p><strong>Сумма:</strong> {(order.total || 0).toLocaleString()} ₽</p>
                        <p><strong>Товаров:</strong> {order.items?.length || 0}</p>
                      </div>

                      {/* Управление статусом заказа */}
                      <div className="order-controls">
                        <select
                          value={order.status || 'pending'}
                          onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                          className="status-select"
                        >
                          {statusOptions.map(option => (
                            <option key={option.value} value={option.value}>
                              {option.label}
                            </option>
                          ))}
                        </select>
                      </div>
                    </div>
                  ))
                )}
              </div>
            )}
          </section>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default AdminPanel;
