import React, { useState, useEffect, useRef } from 'react';
import { FiShoppingCart, FiSearch, FiMenu, FiX, FiSettings, FiUser } from 'react-icons/fi';
import { Link, useLocation } from 'react-router-dom';
import '../styles/main.css';
import '../styles/header.css';
import { useCart } from '../context/CartContext';

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const { cartCount, user, isAuthenticated } = useCart();
  const headerRef = useRef(null);
  const location = useLocation();

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  // Закрытие меню при клике вне его области
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (headerRef.current && !headerRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  // Закрытие меню при смене страницы
  useEffect(() => {
    closeMenu();
  }, [location]);

  return (
    <header className="header" ref={headerRef}>
      <div className="header-container container">
        {/* Мобильное меню */}
        <button 
          className="mobile-menu-button" 
          onClick={toggleMenu}
          aria-label={isMenuOpen ? "Закрыть меню" : "Открыть меню"}
          aria-expanded={isMenuOpen}
        >
          {isMenuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
        </button>

        {/* Логотип */}
        <Link to="/" className="logo" onClick={closeMenu}>
          LI-NING
        </Link>
        
        {/* Основное меню и поиск */}
        <div className={`nav-content ${isMenuOpen ? 'active' : ''}`}>
          <nav className="nav-menu">
            <Link 
              to="/" 
              className={`nav-link ${location.pathname === '/' ? 'active' : ''}`} 
              onClick={closeMenu}
            >
              Главная
            </Link>
            <Link 
              to="/products" 
              className={`nav-link ${location.pathname === '/products' ? 'active' : ''}`} 
              onClick={closeMenu}
            >
              Товары
            </Link>
            <Link 
              to="/about" 
              className={`nav-link ${location.pathname === '/about' ? 'active' : ''}`} 
              onClick={closeMenu}
            >
              О нас
            </Link>
            
            {/* Ссылка на админ-панель (только для админов) */}
            {user?.isAdmin && (
              <Link 
                to="/admin" 
                className={`nav-link admin-link ${location.pathname === '/admin' ? 'active' : ''}`} 
                onClick={closeMenu}
              >
                <FiSettings size={20} style={{ marginRight: 5 }} />
                Админ-панель
              </Link>
            )}
          </nav>

          <div className="search-cart-wrapper">
            {/* Поиск */}
            <div className="search-container">
              <FiSearch className="search-icon" />
              <input
                type="text"
                placeholder="Поиск..."
                className="search-input"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            </div>

            {/* Корзина и профиль */}
            <div className="header-actions">
              <Link 
                to="/cart" 
                className="cart-button" 
                onClick={closeMenu}
                aria-label="Корзина"
              >
                <FiShoppingCart size={20} />
                {cartCount > 0 && (
                  <span className="cart-count">{cartCount}</span>
                )}
              </Link>
              
              <Link 
                to={isAuthenticated ? "/profile" : "/auth"} 
                className="profile-button"
                onClick={closeMenu}
                aria-label={isAuthenticated ? "Профиль" : "Войти"}
              >
                <FiUser size={20} />
                {isAuthenticated && user?.name && (
                  <span className="user-name">{user.name.split(' ')[0]}</span>
                )}
              </Link>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}
