import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Auth.css';
import { FiUser, FiMail, FiLock, FiLogIn, FiUserPlus, FiAlertCircle, FiEye, FiEyeOff } from 'react-icons/fi';
import { motion } from 'framer-motion';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [showPassword, setShowPassword] = useState(false); // üëÄ –°–æ—Å—Ç–æ—è–Ω–∏–µ –¥–ª—è –æ—Ç–æ–±—Ä–∞–∂–µ–Ω–∏—è –ø–∞—Ä–æ–ª—è
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login, register } = useCart();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    
    if (!isLogin) {
      if (!formData.name.trim()) {
        setError('–í–≤–µ–¥–∏—Ç–µ –∏–º—è');
        return;
      }
      if (formData.password !== formData.confirmPassword) {
        setError('–ü–∞—Ä–æ–ª–∏ –Ω–µ —Å–æ–≤–ø–∞–¥–∞—é—Ç');
        return;
      }
      if (formData.password.length < 6) {
        setError('–ü–∞—Ä–æ–ª—å –¥–æ–ª–∂–µ–Ω —Å–æ–¥–µ—Ä–∂–∞—Ç—å –º–∏–Ω–∏–º—É–º 6 —Å–∏–º–≤–æ–ª–æ–≤');
        return;
      }
    }

    if (!formData.email || !formData.password) {
      setError('–ó–∞–ø–æ–ª–Ω–∏—Ç–µ –≤—Å–µ –æ–±—è–∑–∞—Ç–µ–ª—å–Ω—ã–µ –ø–æ–ª—è');
      return;
    }

    setIsLoading(true);

    try {
  const result = isLogin
    ? await login(formData.email, formData.password)
    : await register(formData.name, formData.email, formData.password);

  if (!result || !result.user || !result.token) {
    throw new Error('–ù–µ–≤–µ—Ä–Ω—ã–π –æ—Ç–≤–µ—Ç –æ—Ç —Å–µ—Ä–≤–µ—Ä–∞');
  }

  const { user } = result;

  if (user.isAdmin) {
    toast.success('–í—ã –≤–æ—à–ª–∏ –∫–∞–∫ –∞–¥–º–∏–Ω–∏—Å—Ç—Ä–∞—Ç–æ—Ä!');
    navigate('/admin');
  } else {
    toast.success(isLogin ? '–í—Ö–æ–¥ –≤—ã–ø–æ–ª–Ω–µ–Ω —É—Å–ø–µ—à–Ω–æ!' : '–†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –ø—Ä–æ—à–ª–∞ —É—Å–ø–µ—à–Ω–æ!');
    navigate(isLogin ? '/profile' : '/profile');
  }
} catch (err) {
  setError(err.message);
  toast.error(err.message);
}
  };

  return (
    <div className="app">
      <Header />
      <main className="auth-page">
        <div className="auth-form">
          <h1>
            {isLogin ? (
              <>
                <FiLogIn size={24} style={{ marginRight: 10 }} />
                –í–•–û–î
              </>
            ) : (
              <>
                <FiUserPlus size={24} style={{ marginRight: 10 }} />
                –†–ï–ì–ò–°–¢–†–ê–¶–ò–Ø
              </>
            )}
          </h1>

          {error && (
            <motion.div 
              className="auth-error"
              initial={{ opacity: 0, y: -20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.3 }}
            >
              <FiAlertCircle className="error-icon" />
              <p>{error}</p>
            </motion.div>
          )}

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <div className="input-group">
                <FiUser className="input-icon" />
                <input
                  type="text"
                  name="name"
                  placeholder="–ò–º—è"
                  value={formData.name}
                  onChange={handleChange}
                  minLength="2"
                  required
                />
              </div>
            )}

            <div className="input-group">
              <FiMail className="input-icon" />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            {/* –ü–æ–ª–µ –¥–ª—è –ø–∞—Ä–æ–ª—è —Å –∫–Ω–æ–ø–∫–æ–π –ø—Ä–æ—Å–º–æ—Ç—Ä–∞ */}
            <div className="input-group password-input">
              <FiLock className="input-icon" />
              <input
                type={showPassword ? 'text' : 'password'} // üëÄ –ü–æ–∫–∞–∑—ã–≤–∞–µ–º –∏–ª–∏ —Å–∫—Ä—ã–≤–∞–µ–º
                name="password"
                placeholder="–ü–∞—Ä–æ–ª—å"
                value={formData.password}
                onChange={handleChange}
                required
                minLength="6"
              />
              <button
                type="button"
                className="toggle-password"
                onClick={() => setShowPassword(!showPassword)}
                aria-label={showPassword ? '–°–∫—Ä—ã—Ç—å –ø–∞—Ä–æ–ª—å' : '–ü–æ–∫–∞–∑–∞—Ç—å –ø–∞—Ä–æ–ª—å'}
              >
                {showPassword ? <FiEyeOff /> : <FiEye />}
              </button>
            </div>

            {!isLogin && (
              <div className="input-group password-input">
                <FiLock className="input-icon" />
                <input
                  type={showPassword ? 'text' : 'password'} // üëÄ –¢–æ –∂–µ —Å–∞–º–æ–µ –¥–ª—è –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏—è
                  name="confirmPassword"
                  placeholder="–ü–æ–≤—Ç–æ—Ä–∏—Ç–µ –ø–∞—Ä–æ–ª—å"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                  minLength="6"
                />
                <button
                  type="button"
                  className="toggle-password"
                  onClick={() => setShowPassword(!showPassword)}
                  aria-label={showPassword ? '–°–∫—Ä—ã—Ç—å –ø–∞—Ä–æ–ª—å' : '–ü–æ–∫–∞–∑–∞—Ç—å –ø–∞—Ä–æ–ª—å'}
                >
                  {showPassword ? <FiEyeOff /> : <FiEye />}
                </button>
              </div>
            )}

            <button 
              type="submit" 
              disabled={isLoading}
              className={`auth-button ${isLoading ? 'loading' : ''}`}
            >
              {isLoading ? '–ó–∞–≥—Ä—É–∑–∫–∞...' : isLogin ? '–í–æ–π—Ç–∏' : '–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è'}
            </button>
          </form>

          <p className="toggle-auth" onClick={() => {
            setIsLogin(!isLogin);
            setError('');
            setFormData({
              name: '',
              email: '',
              password: '',
              confirmPassword: ''
            });
          }}>
            {isLogin ? (
              <>–ù–µ—Ç –∞–∫–∫–∞—É–Ω—Ç–∞? <span>–ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä—É–π—Ç–µ—Å—å</span></>
            ) : (
              <>–£–∂–µ –µ—Å—Ç—å –∞–∫–∫–∞—É–Ω—Ç? <span>–í–æ–π–¥–∏—Ç–µ</span></>
            )}
          </p>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Auth;
