import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useLocation } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';
import '../styles/Products.css';
import '../styles/product-card.css';

const allProducts = {
    // Коллекции
    trend: [
      {
        id: 101,
        name: 'Кроссовки Storm Pro',
        price: 12990,
        image: '/assets/trend/storm-pro.jpg',
        isNew: true
      },
      {
        id: 102,
        name: 'Футболка Urban Black',
        price: 3490,
        image: '/assets/trend/urban-black.jpg'
      }
    ],
  
    pantone: [
      {
        id: 201,
        name: 'Кроссовки Neon Pulse',
        price: 14990,
        image: '/assets/pantone/neon-pulse.jpg',
        isNew: true
      },
      {
        id: 202,
        name: 'Рюкзак Color Splash',
        price: 5990,
        image: '/assets/pantone/color-splash.jpg'
      }
    ],
  
    // Категория "Бег"
    бег: {
      кроссовки: [
        {
          id: 301,
          name: 'Кроссовки Air Runner',
          price: 8990,
          image: '/assets/run/shoes-1.jpg',
          description: 'Для длинных дистанций'
        },
        {
          id: 302,
          name: 'Кроссовки Trail Master',
          price: 10990,
          image: '/assets/run/shoes-2.jpg',
          description: 'Для трейлраннинга'
        }
      ],
      футболки: [
        {
          id: 311,
          name: 'Футболка Dry Fit',
          price: 2990,
          image: '/assets/run/shirt-1.jpg'
        },
        {
          id: 312,
          name: 'Футболка Air Mesh',
          price: 3490,
          image: '/assets/run/shirt-2.jpg'
        }
      ],
      шорты: [
        {
          id: 321,
          name: 'Шорты Quick Dry',
          price: 3990,
          image: '/assets/run/shorts-1.jpg'
        }
      ]
    },
  
    // Категория "Баскетбол"
    баскетбол: {
      кроссовки: [
        {
          id: 401,
          name: 'Кроссовки Jump Pro',
          price: 11990,
          image: '/assets/basketball/shoes-1.jpg'
        }
      ],
      майки: [
        {
          id: 411,
          name: 'Майка Team Edition',
          price: 2490,
          image: '/assets/basketball/jersey-1.jpg'
        }
      ]
    },
  
    // Категория "Йога"
    йога: {
      коврики: [
        {
          id: 501,
          name: 'Коврик Pro Yoga',
          price: 4990,
          image: '/assets/yoga/mat-1.jpg'
        }
      ],
      одежда: [
        {
          id: 511,
          name: 'Леггинсы Flex',
          price: 4490,
          image: '/assets/yoga/pants-1.jpg'
        }
      ]
    },
  
    // Категория "Уличный стиль"
    'уличный стиль': {
      кроссовки: [
        {
          id: 601,
          name: 'Кроссовки Street King',
          price: 9990,
          image: '/assets/street/shoes-1.jpg'
        }
      ],
      худи: [
        {
          id: 611,
          name: 'Худи Oversize',
          price: 5990,
          image: '/assets/street/hoodie-1.jpg'
        }
      ]
    }
  };

const Products = () => {
  const location = useLocation();
  const [activeCategory, setActiveCategory] = useState(null);
  const [activeSubcategory, setActiveSubcategory] = useState(null);

  useEffect(() => {
    const params = new URLSearchParams(location.search);
    const category = params.get('category');
    setActiveCategory(category || null);
    setActiveSubcategory(null);
  }, [location]);

  const renderProducts = () => {
    if (!activeCategory) {
      // Показываем все коллекции
      return Object.entries(allProducts).map(([collection, items]) => (
        <section key={collection} className="collection-section">
          <h2 className="collection-title">{collection.toUpperCase()}</h2>
          <div className="products-grid">
            {items.slice(0, 4).map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
          <Link to={`/products?collection=${collection}`} className="view-all">
            Смотреть все
          </Link>
        </section>
      ));
    }

    if (activeCategory && !activeSubcategory) {
      // Показываем подкатегории
      const categoryData = allProducts[activeCategory];
      return (
        <>
          <h1 className="page-title">{activeCategory.toUpperCase()}</h1>
          {Object.keys(categoryData).map(subcategory => (
            <div key={subcategory} className="subcategory-section">
              <h2 
                className="subcategory-title"
                onClick={() => setActiveSubcategory(subcategory)}
              >
                {subcategory.toUpperCase()}
              </h2>
              <div className="products-grid">
                {categoryData[subcategory].slice(0, 4).map(product => (
                  <ProductCard key={product.id} product={product} />
                ))}
              </div>
            </div>
          ))}
        </>
      );
    }

    // Показываем товары выбранной подкатегории
    return (
      <>
        <button 
          onClick={() => setActiveSubcategory(null)}
          className="back-button neon-button"
        >
          ← Назад к {activeCategory}
        </button>
        <h2 className="page-title">{activeSubcategory.toUpperCase()}</h2>
        <div className="products-grid">
          {allProducts[activeCategory][activeSubcategory].map(product => (
            <ProductCard key={product.id} product={product} />
          ))}
        </div>
      </>
    );
  };

  return (
    <>
      <Header />
      <div className="products-page">
        {renderProducts()}
      </div>
      <Footer />
    </>
  );
};

export default Products;
