import { createContext, useContext, useState, useEffect, useRef } from 'react';

const API_BASE_URL = 'http://localhost:5000/api';
const CartContext = createContext();

export function CartProvider({ children }) {
  const [state, setState] = useState({
    cartItems: [],
    user: JSON.parse(localStorage.getItem('user')) || null,
    isAuthenticated: !!localStorage.getItem('user'),
    isLoading: false,
    error: null
  });
  
  const [cartItems, setCartItems] = useState([]);
  const [user, setUser] = useState(null);
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [error, setError] = useState(null);
   const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

    const updateState = (newState) => {
    if (isMounted.current) {
      setState(prev => ({ ...prev, ...newState }));
    }
  };

  // Восстановление состояния при загрузке
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        safeSetState(setUser, parsedUser);
        safeSetState(setIsAuthenticated, true);
        fetchCart(parsedUser.id);
      } catch (e) {
        console.error('Failed to parse stored user', e);
      }
    }

    return () => {
      isMounted.current = false;
    };
  }, []);

  const safeSetState = (setter, value) => {
    if (isMounted.current) {
      setter(value);
    }
  };

  const fetchCart = async (userId) => {
    updateState({ isLoading: true, error: null });
    try {
      const response = await fetch(`${API_BASE_URL}/cart/${userId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });
      
      if (!response.ok) throw new Error('Ошибка загрузки корзины');
      const data = await response.json();
      updateState({ cartItems: data.items || [] });
    } catch (error) {
      updateState({ error: error.message });
      console.error('Ошибка:', error);
    } finally {
      updateState({ isLoading: false });
    }
  };

  const saveCart = async (items) => {
    if (!user) return;
    
    safeSetState(setIsLoading, true);
    safeSetState(setError, null);
    try {
      const response = await fetch(`http://localhost:5000/api/cart/${user.id}`, {
        method: 'POST',
        headers: { 
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ items }),
      });
      
      if (!response.ok) {
        throw new Error('Ошибка сохранения корзины');
      }
    } catch (error) {
      safeSetState(setError, error.message);
      console.error('Ошибка сохранения корзины:', error);
    } finally {
      safeSetState(setIsLoading, false);
    }
  };

  // Добавление товара
  const addToCart = async (product) => {
    if (!user) return;
    
    const existingItem = cartItems.find(item => item.productId === product.id);
    let updatedCart;
    
    if (existingItem) {
      updatedCart = cartItems.map(item => 
        item.productId === product.id 
          ? { ...item, quantity: item.quantity + 1 } 
          : item
      );
    } else {
      updatedCart = [
        ...cartItems,
        {
          ...product,
          cartId: `${product.id}_${Date.now()}`,
          quantity: 1
        }
      ];
    }
    
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Удаление товара
  const removeFromCart = async (cartId) => {
    if (!user) return;
    
    const updatedCart = cartItems.filter(item => item.cartId !== cartId);
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Обновление количества
  const updateQuantity = async (cartId, newQuantity) => {
    if (!user) return;
    
    const updatedCart = cartItems.map(item => 
      item.cartId === cartId 
        ? { ...item, quantity: Math.max(1, newQuantity) } 
        : item
    );
    
    safeSetState(setCartItems, updatedCart);
    await saveCart(updatedCart);
  };

  // Вход пользователя
  const login = (userData) => {
    localStorage.setItem('user', JSON.stringify(userData));
    localStorage.setItem('token', userData.token || '');
    safeSetState(setUser, userData);
    safeSetState(setIsAuthenticated, true);
    fetchCart(userData.id);
  };

  // Выход
  const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');
    safeSetState(setUser, null);
    safeSetState(setIsAuthenticated, false);
    safeSetState(setCartItems, []);
  };

  // Вход администратора (тестовый)
  const adminLogin = async () => {
    safeSetState(setIsLoading, true);
    try {
      const adminUser = {
        id: 'admin-123',
        name: 'Администратор',
        email: 'admin@example.com',
        isAdmin: true,
        token: 'mock-admin-token',
        avatar: 'https://i.pravatar.cc/150?u=admin'
      };
      
      // Сохраняем в localStorage
      localStorage.setItem('user', JSON.stringify(adminUser));
      localStorage.setItem('token', adminUser.token);
      
      // Обновляем состояние
      safeSetState(setUser, adminUser);
      safeSetState(setIsAuthenticated, true);
      await fetchCart(adminUser.id);
      
      return adminUser;
    } catch (error) {
      safeSetState(setError, 'Ошибка входа администратора');
      console.error('Admin login error:', error);
      throw error;
    } finally {
      safeSetState(setIsLoading, false);
    }
  };

  // Проверка прав администратора
  const isAdmin = () => {
    return user?.isAdmin === true;
  };

  return (
    <CartContext.Provider
      value={{
        cartItems,
        cartCount: cartItems.reduce((sum, item) => sum + item.quantity, 0),
        addToCart,
        removeFromCart,
        updateQuantity,
        user,
        isAuthenticated,
        isLoading,
        error,
        login,
        logout,
        adminLogin,
        isAdmin // Добавляем функцию проверки прав
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
