import React from 'react';
import NeonButton from './NeonButton';
import '../styles/product-card.css';
import { useCart } from '../context/CartContext';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const ProductCard = ({ product }) => {
  const { addToCart, isAuthenticated, cartItems } = useCart();

  // Проверяем, есть ли товар уже в корзине
  const isInCart = cartItems.some(item => item.id === product.id);

  const handleAddToCart = () => {
    if (!isAuthenticated) {
      toast.info('Войдите, чтобы добавить товар в корзину', {
        position: "top-right",
        autoClose: 3000,
      });
      return;
    }

    if (isInCart) {
      toast.warning('Товар уже в корзине', {
        position: "top-right",
        autoClose: 2500,
      });
      return;
    }

    addToCart({ ...product, quantity: 1 }); // Добавляем товар с количеством 1
    toast.success(`${product.name} добавлен в корзину`, {
      position: "top-right",
      autoClose: 2000,
    });
  };

  return (
    <div className="product-card">
      <div className="product-image-container">
        <img 
          src={product.image || '/images/placeholder-product.jpg'} 
          alt={product.name} 
          className="product-image"
          onClick={() => window.location.href = `/product/${product.id}`}
        />
        {product.isNew && <span className="product-badge">NEW</span>}
        {product.discount && (
          <span className="discount-badge">-{product.discount}%</span>
        )}
      </div>

      <div className="product-info">
        <h3 className="product-name">{product.name}</h3>
        <div className="price-container">
          {product.oldPrice && (
            <div className="product-old-price">
              {product.oldPrice.toLocaleString()} ₽
            </div>
          )}
          <div className="product-price">
            {product.price.toLocaleString()} ₽
          </div>
        </div>

        <NeonButton 
          text={isInCart ? "В корзине" : "В корзину"} 
          onClick={handleAddToCart}
          disabled={isInCart}
          glowColor={isInCart ? "#4caf50" : "#ff073a"}
          fullWidth
        />
      </div>
    </div>
  );
};

export default ProductCard;
