import React, { useEffect } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut } from 'react-icons/fi'; // ‚úÖ –ü—Ä–∞–≤–∏–ª—å–Ω—ã–µ –∏–º–µ–Ω–∞
import { Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';

const Profile = () => {
  const { user, logout, fetchCurrentUser } = useCart(); // ‚úÖ –¢–µ–ø–µ—Ä—å —ç—Ç–æ –ø—Ä–∞–≤–∏–ª—å–Ω–æ

  useEffect(() => {
    fetchCurrentUser(); // üîÑ –ê–≤—Ç–æ–æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –¥–∞–Ω–Ω—ã—Ö
  }, [fetchCurrentUser]);

  useEffect(() => {
    console.log('–¢–µ–∫—É—â–∏–π –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—å:', user);
  }, [user]);

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">–ü—Ä–æ—Ñ–∏–ª—å</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user?.name || '–ì–æ—Å—Ç—å'}</h2>
            <p><strong>Email:</strong> {user?.email || '–ù–µ —É–∫–∞–∑–∞–Ω'}</p>

            {/* –ö–Ω–æ–ø–∫–∞ –≤—ã—Ö–æ–¥–∞ */}
            <Link to="/auth" onClick={logout}>
              <NeonButton text="–í—ã–π—Ç–∏" fullWidth variant="danger" />
            </Link>
          </div>

          {/* –ó–∞–∫–∞–∑—ã */}
          <section className="orders-section">
            <h2 className="section-title">–ú–æ–∏ –∑–∞–∫–∞–∑—ã</h2>
            {user?.orders && user.orders.length > 0 ? (
              <div className="orders-grid">
                {user.orders.map(order => (
                  <div key={order.id} className="order-card">
                    <p><strong>ID:</strong> {order.id}</p>
                    <p><strong>–î–∞—Ç–∞:</strong> {new Date(order.date).toLocaleString()}</p>
                    <p><strong>–°—Ç–∞—Ç—É—Å:</strong> {order.status}</p>
                    <p><strong>–°—É–º–º–∞:</strong> {order.total} ‚ÇΩ</p>
                  </div>
                ))}
              </div>
            ) : (
              <p>–£ –≤–∞—Å –ø–æ–∫–∞ –Ω–µ—Ç –∑–∞–∫–∞–∑–æ–≤.</p>
            )}
          </section>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Profile;
