// OrderSuccess.js

import React from 'react';
import '../styles/OrderSuccess.css';
import { Link } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';

export default function OrderSuccess({ location = {} }) {
  // –ü—Ä–æ–≤–µ—Ä—è–µ–º, –ø—Ä–∏—à—ë–ª –ª–∏ –∑–∞–∫–∞–∑
  const order = location.state?.order;

  // –§–æ—Ä–º–∞—Ç–∏—Ä—É–µ–º –¥–∞—Ç—É –±–µ–∑–æ–ø–∞—Å–Ω–æ
  const formatDate = (dateString) => {
    if (!dateString) return '–ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö';
    const date = new Date(dateString);
    return isNaN(date.getTime()) ? '–ù–µ–≤–µ—Ä–Ω–∞—è –¥–∞—Ç–∞' : date.toLocaleString();
  };

  // –ï—Å–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö ‚Äî –ø–æ–∫–∞–∑—ã–≤–∞–µ–º —Å–æ–æ–±—â–µ–Ω–∏–µ –æ–± —ç—Ç–æ–º
  if (!order) {
    return (
      <div className="order-success">
        <FiCheckCircle size={64} color="#2ecc71" />
        <h1>üö´ –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –æ –∑–∞–∫–∞–∑–µ</h1>
        <p>–í—ã –ø–æ–ø–∞–ª–∏ —Å—é–¥–∞ –±–µ–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω–∏—è –∑–∞–∫–∞–∑–∞.</p>
        <Link to="/cart">–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –∫–æ—Ä–∑–∏–Ω—É</Link>
      </div>
    );
  }

  return (
    <div className="order-success">
      <FiCheckCircle size={64} color="#2ecc71" />
      <h1>üéâ –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h1>
      <p>–°–ø–∞—Å–∏–±–æ –∑–∞ –ø–æ–∫—É–ø–∫—É. –ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è.</p>

      <div className="order-details">
        <p><strong>–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞:</strong> #{order.id}</p>
        <p><strong>–î–∞—Ç–∞:</strong> {formatDate(order.date)}</p>
        <p><strong>–°—Ç–∞—Ç—É—Å:</strong> –û–∂–∏–¥–∞–µ—Ç –æ–±—Ä–∞–±–æ—Ç–∫–∏</p>
        <p><strong>–ò—Ç–æ–≥–æ:</strong> {order.total.toLocaleString()} ‚ÇΩ</p>
      </div>

      <div className="actions">
        <Link to="/profile">–ü–µ—Ä–µ–π—Ç–∏ –∫ –ø—Ä–æ—Ñ–∏–ª—é</Link>
        <Link to="/">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏</Link>
      </div>
    </div>
  );
}
