import React from 'react';
import { useLocation, Link } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';
import '../styles/OrderSuccess.css';

const OrderSuccess = () => {
  const { state } = useLocation();
  const orderId = state?.orderId;
  const orderTotal = state?.orderTotal;

  return (
    <div className="order-success-container">
      <div className="order-success-card">
        <FiCheckCircle size={80} color="#4BB543" />
        <h1>Заказ успешно оформлен!</h1>
        <p>Номер вашего заказа: <strong>#{orderId}</strong></p>
        <p>Сумма заказа: <strong>{orderTotal?.toLocaleString()} ₽</strong></p>
        <p>Статус заказа: <span className="status-processing">В обработке</span></p>
        
        <div className="order-success-actions">
          <Link to="/profile/orders" className="btn-primary">
            Мои заказы
          </Link>
          <Link to="/products" className="btn-secondary">
            Вернуться к покупкам
          </Link>
        </div>
      </div>
    </div>
  );
};

export default OrderSuccess;
