import { createContext, useContext, useState, useEffect } from 'react';

const API_BASE_URL = 'http://localhost:5000/api';
const CartContext = createContext();

// Данные администратора для тестирования
const ADMIN_CREDENTIALS = {
  email: 'admin1@mail.ru',
  password: 'admin12341234'
};

export function CartProvider({ children }) {
  const [state, setState] = useState({
    cartItems: [],
    user: JSON.parse(localStorage.getItem('user')) || null,
    isAuthenticated: !!localStorage.getItem('user'),
    isLoading: false,
    error: null
  });

  // Для защиты от обновления unmounted компонентов
  const isMounted = useRef(true);

  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  const safeSetState = (newState) => {
    if (isMounted.current) {
      setState((prevState) => ({ ...prevState, ...newState }));
    }
  };

  // Загрузка данных пользователя и корзины при монтировании
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      try {
        const parsedUser = JSON.parse(storedUser);
        safeSetState({ user: parsedUser, isAuthenticated: true });
        fetchCart(parsedUser.id);
      } catch (e) {
        console.error('Ошибка парсинга пользователя из localStorage', e);
      }
    }
  }, []);

  // Получение корзины с сервера
  const fetchCart = async (userId) => {
    safeSetState({ isLoading: true, error: null });
    try {
      const response = await fetch(`${API_BASE_URL}/cart/${userId}`, {
        headers: {
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        }
      });

      if (!response.ok) throw new Error('Ошибка загрузки корзины');

      const data = await response.json();
      safeSetState({ cartItems: data.items || [] });
    } catch (error) {
      safeSetState({ error: error.message });
      console.error('Ошибка загрузки корзины:', error);
    } finally {
      safeSetState({ isLoading: false });
    }
  };

  // Сохранение корзины на сервер
  const saveCart = async (items) => {
    if (!state.user) return;

    safeSetState({ isLoading: true, error: null });
    try {
      const response = await fetch(`${API_BASE_URL}/cart/${state.user.id}`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify({ items }),
      });

      if (!response.ok) {
        throw new Error('Ошибка сохранения корзины');
      }
    } catch (error) {
      safeSetState({ error: error.message });
      console.error('Ошибка сохранения корзины:', error);
    } finally {
      safeSetState({ isLoading: false });
    }
  };

  // Добавление товара в корзину
  const addToCart = async (product) => {
    if (!state.user) return;

    const existingItem = state.cartItems.find(
      (item) => item.productId === product.id
    );

    let updatedCart;
    if (existingItem) {
      updatedCart = state.cartItems.map((item) =>
        item.productId === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
    } else {
      updatedCart = [
        ...state.cartItems,
        {
          ...product,
          cartId: `${product.id}_${Date.now()}`,
          quantity: 1
        }
      ];
    }

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
  };

  // Удаление товара из корзины
  const removeFromCart = async (cartId) => {
    if (!state.user) return;

    const updatedCart = state.cartItems.filter(
      (item) => item.cartId !== cartId
    );

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
  };

  // Обновление количества товара
  const updateQuantity = async (cartId, newQuantity) => {
    if (!state.user) return;

    const updatedCart = state.cartItems.map((item) =>
      item.cartId === cartId
        ? { ...item, quantity: Math.max(1, newQuantity) }
        : item
    );

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
  };

  // Вход пользователя или админа
  const login = async (email, password) => {
    safeSetState({ isLoading: true, error: null });

    try {
      // Проверка на админа
      if (
        email === ADMIN_CREDENTIALS.email &&
        password === ADMIN_CREDENTIALS.password
      ) {
        const adminUser = {
          id: 'admin-1',
          name: 'Admin',
          email: ADMIN_CREDENTIALS.email,
          isAdmin: true,
          token: 'mock-admin-token',
          avatar: 'https://i.pravatar.cc/150?u=admin'
        };

        localStorage.setItem('user', JSON.stringify(adminUser));
        localStorage.setItem('token', adminUser.token);

        safeSetState({
          user: adminUser,
          isAuthenticated: true,
          isLoading: false
        });

        await fetchCart(adminUser.id);
        return adminUser;
      }

      // Логика для обычного пользователя
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      if (!response.ok) throw new Error('Ошибка входа');

      const userData = await response.json();
      localStorage.setItem('user', JSON.stringify(userData));
      localStorage.setItem('token', userData.token);

      safeSetState({
        user: userData,
        isAuthenticated: true,
        isLoading: false
      });

      await fetchCart(userData.id);
      return userData;
    } catch (error) {
      safeSetState({ error: error.message });
      console.error('Ошибка входа:', error);
      throw error;
    } finally {
      safeSetState({ isLoading: false });
    }
  };

  // Выход из аккаунта
  const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');

    safeSetState({
      user: null,
      isAuthenticated: false,
      cartItems: []
    });
  };

  return (
    <CartContext.Provider
      value={{
        cartItems: state.cartItems,
        cartCount: state.cartItems.reduce((sum, item) => sum + item.quantity, 0),
        addToCart,
        removeFromCart,
        updateQuantity,
        user: state.user,
        isAuthenticated: state.isAuthenticated,
        isLoading: state.isLoading,
        error: state.error,
        login,
        logout,
        isAdmin: () => state.user?.isAdmin === true
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
};
