import React, { useState, useEffect } from 'react';
import Header from '../components/Header';
import Footer from '../components/Footer';
import NeonButton from '../components/NeonButton';
import '../styles/main.css';
import '../styles/slider.css';
import '../styles/categories.css';

const collections = [
  { 
    id: 1, 
    name: 'TREND', 
    description: '–ù–æ–≤–µ–π—à–∏–µ –º–æ–¥–µ–ª–∏ —Å–µ–∑–æ–Ω–∞',
    bgClass: 'trend-bg'
  },
  { 
    id: 2, 
    name: 'PANTONE', 
    description: '–≠–∫—Å–∫–ª—é–∑–∏–≤–Ω—ã–µ —Ü–≤–µ—Ç–æ–≤—ã–µ —Ä–µ—à–µ–Ω–∏—è',
    bgClass: 'pantone-bg'
  },
  { 
    id: 3, 
    name: 'BADFIVE', 
    description: '–£–ª–∏—á–Ω—ã–π –±–∞—Å–∫–µ—Ç–±–æ–ª',
    bgClass: 'badfive-bg'
  }
];

const categories = [
  { id: 1, name: "–ë–µ–≥", icon: "üèÉ‚Äç‚ôÇÔ∏è" },
  { id: 2, name: "–ë–∞—Å–∫–µ—Ç–±–æ–ª", icon: "üèÄ" },
  { id: 3, name: "–ô–æ–≥–∞", icon: "üßò‚Äç‚ôÄÔ∏è" },
  { id: 4, name: "–£–ª–∏—á–Ω—ã–π —Å—Ç–∏–ª—å", icon: "üõπ" }
];

export default function Home() {
  const [currentSlide, setCurrentSlide] = useState(0);

  // –ê–≤—Ç–æ–ø—Ä–æ–∫—Ä—É—Ç–∫–∞ —Å–ª–∞–π–¥–µ—Ä–∞
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide(prev => (prev + 1) % collections.length);
    }, 5000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="app">
      <Header />

      <main className="main-content">
        {/* –°–ª–∞–π–¥–µ—Ä –∫–æ–ª–ª–µ–∫—Ü–∏–π */}
        <section className="collections-slider">
          {collections.map((collection, index) => (
            <div 
              key={collection.id}
              className={`slide ${index === currentSlide ? 'active' : ''} ${collection.bgClass}`}
            >
              <div className="slide-content">
                <h2 className="collection-name">{collection.name}</h2>
                <p className="collection-desc">{collection.description}</p>
                <NeonButton text="–°–º–æ—Ç—Ä–µ—Ç—å –∫–æ–ª–ª–µ–∫—Ü–∏—é" />
              </div>
            </div>
          ))}
          
          <div className="slider-dots">
            {collections.map((_, index) => (
              <button
                key={index}
                className={`dot ${index === currentSlide ? 'active' : ''}`}
                onClick={() => setCurrentSlide(index)}
              />
            ))}
          </div>
        </section>

        {/* –°–µ—Ç–∫–∞ –∫–∞—Ç–µ–≥–æ—Ä–∏–π */}
        <section className="categories-section">
          <h2 className="section-title">–ö–∞—Ç–µ–≥–æ—Ä–∏–∏ —Ç–æ–≤–∞—Ä–æ–≤</h2>
          <div className="categories-grid">
            {categories.map(category => (
              <div key={category.id} className="category-card">
                <div className="category-icon">{category.icon}</div>
                <h3 className="category-name">{category.name}</h3>
              </div>
            ))}
          </div>
        </section>
      </main>

      <Footer />
    </div>
  );
}
