import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';

const Profile = () => {
  const { user, fetchCurrentUser, logout } = useCart();
  const [loading, setLoading] = useState(true);

  // Автоматически загружаем данные пользователя
  useEffect(() => {
    const loadUser = async () => {
      try {
        await fetchCurrentUser(); // ✅ Теперь это работает!
      } catch (err) {
        console.error('Ошибка загрузки пользователя:', err.message);
      } finally {
        setLoading(false);
      }
    };

    loadUser();
  }, [fetchCurrentUser]);

  if (loading) {
    return <div>Загрузка профиля...</div>;
  }

  if (!user) {
    return (
      <div>
        <Header />
        <main className="profile-page">
          <h2>Вы не авторизованы</h2>
          <p>Пожалуйста, войдите, чтобы увидеть свой профиль.</p>
          <Link to="/auth">Перейти к авторизации</Link>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">Профиль</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user?.name}</h2>
            <p><strong>Email:</strong> {user?.email}</p>
            <p><strong>ID:</strong> {user?.id}</p>
            <p><strong>Роль:</strong> {user?.isAdmin ? 'Администратор' : 'Пользователь'}</p>

            <Li onClick={logout}>
              <FiLogOut /> Выйти
            </Li>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Profile;
