import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Auth.css';
import { FiUser, FiMail, FiLock, FiLogIn, FiUserPlus } from 'react-icons/fi';

const Auth = () => {
  const [isLogin, setIsLogin] = useState(true);
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const { login } = useCart();
  const navigate = useNavigate();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);

    try {
      // Имитация задержки сети
      await new Promise(resolve => setTimeout(resolve, 800));
      
      if (isLogin) {
        // Вход (замените на реальный API-запрос)
        const mockUser = {
          id: Date.now(),
          name: 'Пользователь',
          email: formData.email,
          isAdmin: false,
          avatar: `https://i.pravatar.cc/150?u=${formData.email}`
        };
        login(mockUser);
        navigate('/profile');
      } else {
        // Регистрация (замените на реальный API-запрос)
        const newUser = {
          id: Date.now(),
          name: formData.name,
          email: formData.email,
          isAdmin: false,
          avatar: `https://i.pravatar.cc/150?u=${formData.email}`
        };
        login(newUser);
        navigate('/profile');
      }
    } catch (err) {
      setError(isLogin 
        ? 'Неверный email или пароль' 
        : 'Пользователь с таким email уже существует');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="app">
      <Header />
      <main className="auth-page">
        <div className="auth-form">
          <h1>
            {isLogin ? (
              <>
                <FiLogIn size={24} style={{ marginRight: 10 }} />
                ВХОД
              </>
            ) : (
              <>
                <FiUserPlus size={24} style={{ marginRight: 10 }} />
                РЕГИСТРАЦИЯ
              </>
            )}
          </h1>

          {error && (
            <div className="auth-error">
              <p>{error}</p>
            </div>
          )}

          <form onSubmit={handleSubmit}>
            {!isLogin && (
              <div className="input-group">
                <FiUser className="input-icon" />
                <input
                  type="text"
                  name="name"
                  placeholder="Имя"
                  value={formData.name}
                  onChange={handleChange}
                  required
                  minLength="2"
                />
              </div>
            )}

            <div className="input-group">
              <FiMail className="input-icon" />
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>

            <div className="input-group">
              <FiLock className="input-icon" />
              <input
                type="password"
                name="password"
                placeholder="Пароль"
                value={formData.password}
                onChange={handleChange}
                required
                minLength="6"
              />
            </div>

            <button 
              type="submit" 
              disabled={isLoading}
              className={isLoading ? 'loading' : ''}
            >
              {isLoading ? (
                'Загрузка...'
              ) : isLogin ? (
                'Войти'
              ) : (
                'Зарегистрироваться'
              )}
            </button>
          </form>

          <p className="toggle-auth" onClick={() => setIsLogin(!isLogin)}>
            {isLogin ? (
              <>Нет аккаунта? <span>Зарегистрируйтесь</span></>
            ) : (
              <>Уже есть аккаунт? <span>Войдите</span></>
            )}
          </p>
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Auth;
