import React from 'react';
import { useCart } from '../context/CartContext';
import { Link, useNavigate } from 'react-router-dom';
import NeonButton from '../components/NeonButton';
import '../styles/Cart.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Cart = () => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity,
    cartCount,
    user,
    isAuthenticated,
    isLoading,
    error
  } = useCart();
  
  const navigate = useNavigate();
  const totalPrice = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  const handleCheckout = () => {
    if (!isAuthenticated) {
      toast.info('Для оформления заказа войдите в систему', {
        position: "top-right",
        autoClose: 3000
      });
      navigate('/auth?return=/cart');
      return;
    }
    
    if (cartCount === 0) {
      toast.warning('Ваша корзина пуста', {
        position: "top-right",
        autoClose: 3000
      });
      return;
    }
    
    navigate('/delivery');
  };

  if (isLoading) return <div className="loading">Загрузка корзины...</div>;
  if (error) return <div className="error">Ошибка: {error}</div>;

  return (
    <div className="cart-content">
      <div className="cart-page">
        <h1>Корзина {user && `(${user.name})`}</h1>
        
        {!isAuthenticated && (
          <div className="auth-warning">
            <p>Войдите в систему для оформления заказа</p>
            <Link to="/auth" className="auth-btn">Войти</Link>
          </div>
        )}

        {cartCount === 0 ? (
          <div className="empty-cart">
            <p>Ваша корзина пуста</p>
            <Link to="/products" className="browse-btn">
              Перейти к товарам
            </Link>
          </div>
        ) : (
          <div className="cart-container">
            <div className="cart-items">
              {cartItems.map(item => (
                <div key={item.cartId} className="cart-item">
                  <img 
                    src={item.image} 
                    alt={item.name} 
                    className="item-image"
                  />
                  <div className="item-details">
                    <h3 className="item-name">{item.name}</h3>
                    <div className="item-price">{item.price.toLocaleString()} ₽</div>
                    <div className="quantity-control">
                      <button 
                        onClick={() => updateQuantity(item.cartId, item.quantity - 1)}
                        disabled={item.quantity <= 1}
                      >
                        -
                      </button>
                      <input
                        type="number"
                        value={item.quantity}
                        onChange={(e) => 
                          updateQuantity(item.cartId, parseInt(e.target.value) || 1)
                        }
                        min="1"
                      />
                      <button 
                        onClick={() => updateQuantity(item.cartId, item.quantity + 1)}
                      >
                        +
                      </button>
                    </div>
                  </div>
                  <button 
                    onClick={() => {
                      removeFromCart(item.cartId);
                      toast.success(`${item.name} удален из корзины`);
                    }}
                    className="remove-btn"
                    aria-label="Удалить"
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>

            <div className="cart-summary">
              <div className="summary-details">
                <h3>Детали заказа</h3>
                <div className="summary-row">
                  <span>Товары ({cartCount})</span>
                  <span>{totalPrice.toLocaleString()} ₽</span>
                </div>
                <div className="summary-row">
                  <span>Доставка</span>
                  <span>Бесплатно</span>
                </div>
                <div className="summary-total">
                  <span>Итого</span>
                  <span>{totalPrice.toLocaleString()} ₽</span>
                </div>
              </div>
              
              <NeonButton 
                text="Оформить заказ" 
                className="checkout-btn"
                onClick={handleCheckout}
                disabled={!isAuthenticated || cartCount === 0}
                glowColor="#ff073a"
              />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Cart;
