import { motion } from 'framer-motion'

export default function NeonButton({ text }) {
  return (
    <motion.button
      whileHover={{ 
        scale: 1.05,
        boxShadow: "0 0 15px rgba(0, 255, 238, 0.7)"
      }}
      whileTap={{ scale: 0.95 }}
      className="px-8 py-3 border border-cyan-400 text-cyan-400 font-mono text-lg relative overflow-hidden"
    >
      <span className="absolute inset-0 bg-cyan-400 opacity-0 hover:opacity-10 transition" />
      {text}
    </motion.button>
  )
}
