import React from 'react';
import { useNavigate } from 'react-router-dom';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Cart.css';

const Cart = () => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity,
    isAuthenticated,
    createOrder,
    user
  } = useCart();
  const navigate = useNavigate();

  const totalPrice = cartItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);

  const handleCheckout = async () => {
    if (!isAuthenticated) {
      alert('Для оформления заказа войдите в систему');
      navigate('/auth?return=cart');
      return;
    }
    
    const order = await createOrder();
    if (order) {
      alert(`Заказ #${order.id.slice(0, 8)} успешно оформлен!`);
      navigate('/profile/orders');
    } else {
      alert('Произошла ошибка при оформлении заказа');
    }
  };

  const handleQuantityChange = (cartId, e) => {
    const newQuantity = parseInt(e.target.value) || 1;
    updateQuantity(cartId, Math.max(1, newQuantity));
  };

  return (
    <div className="app-wrapper">
      <Header />
      <main className="cart-content">
        <div className="cart-page container">
          <h1>Корзина {user && `(${user.displayName || user.email})`}</h1>
          
          {!isAuthenticated && (
            <div className="auth-warning">
              <p>Войдите в систему, чтобы сохранить корзину и оформить заказ</p>
              <button 
                onClick={() => navigate('/auth?return=cart')}
                className="auth-btn"
              >
                Войти или зарегистрироваться
              </button>
            </div>
          )}

          {cartItems.length === 0 ? (
            <div className="empty-cart">
              <p>Ваша корзина пуста</p>
              <button 
                onClick={() => navigate('/products')}
                className="browse-btn"
              >
                Перейти к товарам
              </button>
            </div>
          ) : (
            <div className="cart-container">
              <div className="cart-items">
                {cartItems.map(item => (
                  <div key={item.cartId} className="cart-item">
                    <img src={item.image} alt={item.name} className="item-image" />
                    <div className="item-details">
                      <h3 className="item-name">{item.name}</h3>
                      <p className="item-price">{item.price.toLocaleString()} ₽</p>
                      
                      <div className="quantity-control">
                        <button 
                          onClick={() => updateQuantity(item.cartId, item.quantity - 1)}
                          disabled={item.quantity <= 1}
                        >
                          -
                        </button>
                        <input
                          type="number"
                          value={item.quantity}
                          onChange={(e) => handleQuantityChange(item.cartId, e)}
                          min="1"
                        />
                        <button 
                          onClick={() => updateQuantity(item.cartId, item.quantity + 1)}
                        >
                          +
                        </button>
                      </div>
                    </div>
                    <button 
                      onClick={() => removeFromCart(item.cartId)}
                      className="remove-btn"
                      aria-label="Удалить"
                    >
                      ×
                    </button>
                  </div>
                ))}
              </div>

              <div className="cart-summary">
                <div className="summary-details">
                  <h3>Детали заказа</h3>
                  <div className="summary-row">
                    <span>Товары ({cartItems.reduce((sum, item) => sum + item.quantity, 0)})</span>
                    <span>{totalPrice.toLocaleString()} ₽</span>
                  </div>
                  <div className="summary-row">
                    <span>Доставка</span>
                    <span>Бесплатно</span>
                  </div>
                  <div className="summary-total">
                    <span>Итого</span>
                    <span>{totalPrice.toLocaleString()} ₽</span>
                  </div>
                </div>
                
                <button 
                  onClick={handleCheckout}
                  className="checkout-btn"
                  disabled={cartItems.length === 0}
                >
                  Оформить заказ
                </button>
              </div>
            </div>
          )}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default Cart;
