import React, { useEffect } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';

const Profile = () => {
  const { user, logout, fetchCurrentUser } = useCart();

  useEffect(() => {
    if (typeof fetchCurrentUser === 'function') {
      fetchCurrentUser();
    } else {
      console.error('fetchCurrentUser не определена в контексте');
    }
  }, [fetchCurrentUser]);

  useEffect(() => {
    console.log('Текущий пользователь:', user);
  }, [user]);

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">Профиль</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user?.name || 'Гость'}</h2>
            <p><strong>Email:</strong> {user?.email || 'Не указан'}</p>

            {/* Кнопка выхода */}
            <Link to="/auth" onClick={logout}>
              <NeonButton text="Выйти" fullWidth variant="danger" />
            </Link>
          </div>

          {/* Заказы */}
          <section className="orders-section">
            <h2 className="section-title">Мои заказы</h2>
            {user?.orders && user.orders.length > 0 ? (
              <div className="orders-grid">
                {user.orders.map(order => (
                  <div key={order.id} className="order-card">
                    <p><strong>ID:</strong> {order.id}</p>
                    <p><strong>Дата:</strong> {new Date(order.date).toLocaleString()}</p>
                    <p><strong>Статус:</strong> {order.status}</p>
                    <p><strong>Сумма:</strong> {order.total} ₽</p>
                  </div>
                ))}
              </div>
            ) : (
              <p>У вас пока нет заказов.</p>
            )}
          </section>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Profile;
