import React from 'react';
import { useCart } from '../context/CartContext';
import { Link, useNavigate } from 'react-router-dom';
import NeonButton from '../components/NeonButton';
import '../styles/Cart.css';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import { FiShoppingCart, FiArrowRight, FiTrash2 } from 'react-icons/fi';

const Cart = () => {
  const { 
    cartItems, 
    removeFromCart, 
    updateQuantity,
    cartCount,
    User,
    isAuthenticated,
    isLoading,
    error
  } = useCart();

  const navigate = useNavigate();
  
  // Защита от undefined и null
  const validCartItems = Array.isArray(cartItems) ? cartItems : [];

  const totalPrice = validCartItems.reduce(
    (sum, item) => sum + (item.price * item.quantity),
    0
  );

  const handleCheckout = () => {
    if (!isAuthenticated) {
      toast.info('Для оформления заказа войдите в систему', {
        position: "top-right",
        autoClose: 3000
      });
      navigate('/auth?return=/cart');
      return;
    }
    
    if (validCartItems.length === 0) {
      toast.warning('Ваша корзина пуста', {
        position: "top-right",
        autoClose: 3000
      });
      return;
    }
    
    navigate('/delivery');
  };

  const handleRemoveItem = (item) => {
    removeFromCart(item.id);
    toast.success(`${item.name} удален из корзины`, {
      position: "top-right",
      autoClose: 2000
    });
  };

  if (isLoading) return (
    <div className="cart-loading">
      <div className="loading-spinner"></div>
      <p>Загрузка корзины...</p>
    </div>
  );

  if (error) return (
    <div className="cart-error">
      <FiShoppingCart size={48} />
      <p>Ошибка: {error}</p>
      <button onClick={() => window.location.reload()} className="reload-btn">
        Попробовать снова
      </button>
    </div>
  );

  return (
    <div className="cart-container">
      <div className="cart-header">
        <h1>
          <FiShoppingCart />
          Корзина {User && <span>({User.name})</span>}
        </h1>
      </div>
      
      {!isAuthenticated && (
        <div className="auth-notice">
          <p>Войдите в систему для сохранения корзины и оформления заказа</p>
          <Link to="/auth" className="auth-link">
            Войти или зарегистрироваться <FiArrowRight />
          </Link>
        </div>
      )}

      {validCartItems.length === 0 ? (
        <div className="empty-cart">
          <FiShoppingCart size={64} className="empty-cart-icon" />
          <h2>Ваша корзина пуста</h2>
          <p>Найдите что-нибудь интересное в нашем каталоге</p>
          <Link to="/products" className="browse-products-btn">
            Перейти к товарам
          </Link>
        </div>
      ) : (
        <div className="cart-content">
          <div className="cart-items-list">
            {validCartItems.map(item => (
              <div key={item.id || item.cartId} className="cart-item">
                <img 
                  src={item.image || '/images/placeholder-product.jpg'} 
                  alt={item.name} 
                  className="item-image"
                  onClick={() => navigate(`/product/${item.productId || item.id}`)}
                />
                <div className="item-info">
                  <h3 onClick={() => navigate(`/product/${item.productId || item.id}`)}>
                    {item.name}
                  </h3>
                  <div className="item-price">{item.price.toLocaleString()} ₽</div>
                  
                  <div className="quantity-controls">
                    <button 
                      onClick={() => updateQuantity(item.id, item.quantity - 1)}
                      disabled={item.quantity <= 1}
                      className="quantity-btn minus"
                    >
                      −
                    </button>
                    <span className="quantity-value">{item.quantity}</span>
                    <button 
                      onClick={() => updateQuantity(item.id, item.quantity + 1)}
                      className="quantity-btn plus"
                    >
                      +
                    </button>
                  </div>
                </div>
                <button 
                  onClick={() => handleRemoveItem(item)} 
                  className="remove-button"
                  aria-label="Удалить товар"
                >
                  <FiTrash2 size={20} color="#e74c3c" />
                </button>
              </div>
            ))}
          </div>

          <div className="cart-summary">
            <h3>Сумма заказа</h3>
            <div className="summary-details">
              <div className="summary-row">
                <span>Товары ({validCartItems.length})</span>
                <span>{totalPrice.toLocaleString()} ₽</span>
              </div>
              <div className="summary-row">
                <span>Доставка</span>
                <span>Бесплатно</span>
              </div>
              <div className="summary-total">
                <span>Итого к оплате</span>
                <span>{totalPrice.toLocaleString()} ₽</span>
              </div>
            </div>

            <NeonButton 
              onClick={handleCheckout}
              disabled={!isAuthenticated || validCartItems.length === 0}
              fullWidth
            >
              Оформить заказ
            </NeonButton>
            
            {!isAuthenticated && (
              <p className="checkout-note">
                Для оформления заказа требуется войти в систему
              </p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
