import React, { useState, useEffect, useRef } from 'react';
import { FiShoppingCart, FiSearch, FiMenu, FiX, FiSettings, FiUser, FiInfo } from 'react-icons/fi';
import { Link, useLocation } from 'react-router-dom';
import '../styles/main.css';
import '../styles/header.css';
import { useCart } from '../context/CartContext';

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [showAdminTooltip, setShowAdminTooltip] = useState(false);
  const { cartCount, user, isAuthenticated } = useCart();
  const headerRef = useRef(null);
  const location = useLocation();

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  // Закрытие меню при клике вне его области
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (headerRef.current && !headerRef.current.contains(event.target)) {
        setIsMenuOpen(false);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  return (
    <header className="header" ref={headerRef}>
      <div className="header-container container">
        {/* Мобильное меню */}
        <button 
          className="mobile-menu-button" 
          onClick={toggleMenu}
          aria-label={isMenuOpen ? "Закрыть меню" : "Открыть меню"}
          aria-expanded={isMenuOpen}
        >
          {isMenuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
        </button>

        {/* Логотип */}
        <Link to="/" className="logo" onClick={closeMenu}>
          LI-NING
        </Link>
        
        {/* Основное меню и поиск */}
        <div className={`nav-content ${isMenuOpen ? 'active' : ''}`}>
          <nav className="nav-menu">
            <Link to="/" className="nav-link" onClick={closeMenu}>Главная</Link>
            <Link to="/products" className="nav-link" onClick={closeMenu}>Товары</Link>
            <Link to="/about" className="nav-link" onClick={closeMenu}>О нас</Link>
            
            {/* Ссылка на админ-панель с подсказкой */}
            {user?.isAdmin && (
              <div 
                className="admin-link-wrapper"
                onMouseEnter={() => setShowAdminTooltip(true)}
                onMouseLeave={() => setShowAdminTooltip(false)}
                onTouchStart={() => setShowAdminTooltip(!showAdminTooltip)}
              >
                <Link to="/admin" className="nav-link admin-link" onClick={closeMenu}>
                  <FiSettings size={20} style={{ marginRight: 5 }} />
                  Админ-панель
                </Link>
                {showAdminTooltip && (
                  <div className="admin-tooltip">
                    <FiInfo size={16} />
                    <span>Управление заказами и товарами</span>
                  </div>
                )}
              </div>
            )}
          </nav>

          <div className="search-cart-wrapper">
            {/* Поиск и корзина остаются без изменений */}
          </div>
        </div>
      </div>
    </header>
  );
}
