// client/src/pages/Profile.js

import React, { useEffect, useState } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut, FiShoppingBag } from 'react-icons/fi';

const Profile = () => {
  const { user, fetchCurrentUser, logout } = useCart();
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    const loadUser = async () => {
      await fetchCurrentUser();
    };

    const loadOrders = () => {
      const allOrders = JSON.parse(localStorage.getItem('orders')) || [];
      if (user) {
        const userOrders = allOrders.filter(order => order.userId === user.id);
        setOrders(userOrders);
      }
    };

    loadUser();
    loadOrders();
  }, [user, fetchCurrentUser]);

  if (!user) {
    return (
      <div>
        <Header />
        <main className="profile-page">
          <h2>Вы не авторизованы</h2>
          <p>Пожалуйста, войдите, чтобы увидеть свой профиль.</p>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">Профиль</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user.name}</h2>
            <p><strong>Email:</strong> {user.email}</p>
            <p><strong>ID:</strong> {user.id}</p>
            <p><strong>Роль:</strong> {user.isAdmin ? 'Администратор' : 'Пользователь'}</p>

            <button onClick={logout}>
              Выйти
            </button>
          </div>
        </section>

        {/* История заказов */}
        <section className="orders-section">
          <h2 className="section-title">Ваши заказы</h2>
          
          {orders.length === 0 ? (
            <p>У вас пока нет заказов</p>
          ) : (
            <div className="orders-grid">
              {orders.map(order => (
                <div key={order.id} className="order-card">
                  <h3>Заказ #{order.id}</h3>
                  <p><strong>Дата:</strong> {new Date(order.date).toLocaleDateString()}</p>
                  <p><strong>Статус:</strong> {order.status}</p>
                  <p><strong>Итого:</strong> {order.total.toLocaleString()} ₽</p>
                </div>
              ))}
            </div>
          )}
        </section>
      </main>
      <Footer />
    </div>
  );
};
