// client/src/pages/OrderSuccess.js

import React from 'react';
import { useLocation } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';
import '../styles/OrderSuccess.css';

const OrderSuccess = () => {
  const { state } = useLocation();

  // –ï—Å–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö –æ –∑–∞–∫–∞–∑–µ
  if (!state || !state.orderId) {
    return (
      <div className="order-success-container">
        <div className="order-success-card error">
          <h2>–ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ</h2>
          <p>–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–µ—Ä–Ω–∏—Ç–µ—Å—å –≤ –∫–æ—Ä–∑–∏–Ω—É –∏ –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –æ—Ñ–æ—Ä–º–∏—Ç—å –∑–∞–∫–∞–∑ —Å–Ω–æ–≤–∞</p>
          <a href="/cart" className="btn-primary">–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –∫–æ—Ä–∑–∏–Ω—É</a>
        </div>
      </div>
    );
  }

  return (
    <div className="order-success-container">
      <div className="order-success-card">
        <FiCheckCircle size={80} color="#4BB543" />
        <h1>üéâ –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h1>
        <p><strong>–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞:</strong> #{state.orderId}</p>
        <p><strong>–°—É–º–º–∞:</strong> {state.orderTotal?.toLocaleString()} ‚ÇΩ</p>
        <p>
          –°—Ç–∞—Ç—É—Å:
          <span className="status-processing">–í –æ–±—Ä–∞–±–æ—Ç–∫–µ</span>
        </p>

        <div className="order-success-actions">
          <a href="/profile" className="btn-primary">–ú–æ–∏ –∑–∞–∫–∞–∑—ã</a>
          <a href="/" className="btn-secondary">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏</a>
        </div>
      </div>
    </div>
  );
};

export default OrderSuccess;
