import React, { useState } from 'react';
import { FiShoppingCart, FiSearch, FiMenu, FiX } from 'react-icons/fi';
import '../styles/main.css';
import '../styles/header.css';

export default function Header() {
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  return (
    <header className="header">
      <div className="header-container container">
        {/* Мобильное меню - кнопка (видно только на мобильных) */}
        <button className="mobile-menu-button" onClick={toggleMenu}>
          {isMenuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
        </button>

        {/* Логотип */}
        <div className="logo">LI-NING</div>
        
        {/* Основное меню */}
        <nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <a href="/shop" className="nav-link">Товары</a>
          <a href="/about" className="nav-link">О нас</a>
          <a href="/login" className="nav-link">Вход</a>
        </nav>

        {/* Поиск и корзина */}
        <div className="search-cart-wrapper">
          <button className="cart-button">
            <FiShoppingCart size={20} />
          </button>
          <div className={`search-container ${isMenuOpen ? 'mobile-visible' : ''}`}>
            <input
              type="text"
              placeholder="Поиск..."
              className="search-input"
            />
            <FiSearch className="search-icon" />
          </div>
        </div>
      </div>
    </header>
  );
}
