// client/src/pages/OrderSuccess.js

import React from 'react';
import { useLocation } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';
import '../styles/OrderSuccess.css';
import { Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';

const OrderSuccess = () => {
  const { state } = useLocation();

  // –ï—Å–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö –æ –∑–∞–∫–∞–∑–µ
  if (!state || !state.order) {
    return (
      <div className="order-success-container">
        <div className="order-success-card error">
          <h2>üö´ –ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ</h2>
          <p>–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –≤–µ—Ä–Ω–∏—Ç–µ—Å—å –≤ –∫–æ—Ä–∑–∏–Ω—É –∏ –ø–æ–ø—Ä–æ–±—É–π—Ç–µ –æ—Ñ–æ—Ä–º–∏—Ç—å –∑–∞–∫–∞–∑ —Å–Ω–æ–≤–∞</p>
          <Link to="/cart">
            <NeonButton text="–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –∫–æ—Ä–∑–∏–Ω—É" fullWidth />
          </Link>
        </div>
      </div>
    );
  }

  const { order } = state;

  return (
    <div className="order-success-container">
      <div className="order-success-card">
        <FiCheckCircle size={80} color="#4BB543" />
        <h1> –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h1>
        <p><strong>–ù–æ–º–µ—Ä:</strong> #{order.id}</p>
        <p><strong>–°—É–º–º–∞:</strong> {order.total.toLocaleString()} ‚ÇΩ</p>
        <p><strong>–°—Ç–∞—Ç—É—Å:</strong> <span className="status-badge pending">–û–∂–∏–¥–∞–µ—Ç –æ–±—Ä–∞–±–æ—Ç–∫–∏</span></p>

        <div className="order-success-actions">
          <Link to="/profile">
            <NeonButton text="–ú–æ–∏ –∑–∞–∫–∞–∑—ã" fullWidth danger />
          </Link>
          <Link to="/">
            <NeonButton text="–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏" fullWidth />
          </Link>
        </div>
      </div>
    </div>
  );
};

export default OrderSuccess;
