import React, { useEffect } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Profile.css';
import { FiUser, FiLogOut } from 'react-icons/fi';
import { Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';

const Profile = () => {
  const { user, logout } = useCart();

  // Можно использовать для логики, если потребуется загрузка данных с сервера
  useEffect(() => {
    console.log('Текущий пользователь:', user);
  }, [user]);

  if (!user) {
    return (
      <div className="app">
        <Header />
        <main className="profile-page">
          <section className="profile-section">
            <h1>Профиль</h1>
            <p>Вы не вошли в аккаунт.</p>
            <Link to="/auth">
              <NeonButton text="Войти" fullWidth variant="primary" />
            </Link>
          </section>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="app">
      <Header />
      <main className="profile-page">
        <section className="profile-section">
          <h1 className="page-title">Профиль</h1>

          <div className="profile-card">
            <div className="profile-avatar">
              <FiUser size={60} />
            </div>
            <h2>{user?.name || 'Гость'}</h2>
            <p><strong>Email:</strong> {user?.email || 'Не указан'}</p>
            <p><strong>ID:</strong> {user?._id || 'Нет данных'}</p>
            <p><strong>Роль:</strong> {user?.isAdmin ? 'Администратор' : 'Пользователь'}</p>

            {/* Кнопка выхода */}
            <Link to="/auth" onClick={logout}>
              <NeonButton text="Выйти" fullWidth variant="danger" />
            </Link>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
};

export default Profile;
