import React from 'react';
import { useLocation } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';
import '../styles/OrderSuccess.css';

const OrderSuccess = () => {
  const { state } = useLocation();
  
  // Если нет данных - предлагаем вернуться в корзину
  if (!state?.orderId) {
    return (
      <div className="order-success-container">
        <div className="order-success-card error">
          <h2>Не удалось получить данные заказа</h2>
          <p>Пожалуйста, вернитесь в корзину и попробуйте оформить заказ снова</p>
          <a href="/cart" className="btn-primary">Вернуться в корзину</a>
        </div>
      </div>
    );
  }

  return (
    <div className="order-success-container">
      <div className="order-success-card">
        <FiCheckCircle size={80} color="#4BB543" />
        <h1>Заказ успешно оформлен!</h1>
        <p>Номер вашего заказа: <strong>#{state.orderId}</strong></p>
        <p>Сумма заказа: <strong>{state.orderTotal.toLocaleString()} ₽</strong></p>
        <p>Статус заказа: <span className="status-processing">В обработке</span></p>
        
        <div className="order-success-actions">
          <a href="/profile/orders" className="btn-primary">
            Мои заказы
          </a>
          <a href="/products" className="btn-secondary">
            Вернуться к покупкам
          </a>
        </div>
      </div>
    </div>
  );
};

export default OrderSuccess;
