import React from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/Cart.css';

const Cart = () => {
  const { 
    cartItems, 
    addToCart,
  isAuthenticated,
  user,
  login,
  orders,
  createOrder
  } = useCart();

  const handleLogin = () => {
    // В реальном приложении здесь будет форма входа
    login({
      id: 1,
      name: 'Иван Иванов',
      email: 'user@example.com',
      isAdmin: false
    });
  };

  const handleCheckout = () => {
    if (!isAuthenticated) {
      alert('Пожалуйста, войдите в систему для оформления заказа');
      return;
    }
    
    // Здесь будет логика оформления заказа
    console.log('Заказ оформлен:', {
      user,
      items: cartItems,
      total: cartTotal,
      status: 'pending'
    });
    
    alert('Заказ успешно оформлен!');
  };

  return (
    <div className="app">
      <Header />
      <main className="cart-page">
        <h1>Корзина</h1>
        
        {!isAuthenticated && (
          <div className="auth-notice">
            <p>Для сохранения истории заказов войдите в систему</p>
            <button onClick={handleLogin} className="login-button">
              Войти
            </button>
          </div>
        )}

        {cartItems.length === 0 ? (
          <p>Ваша корзина пуста</p>
        ) : (
          <div className="cart-container">
            <div className="cart-items">
              {cartItems.map(item => (
                <div key={item.id} className="cart-item">
                  <img src={item.image} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-details">
                    <h3>{item.name}</h3>
                    <div className="price">{item.price.toLocaleString()} ₽</div>
                    <div className="quantity-controls">
                      <button onClick={() => updateQuantity(item.id, item.quantity - 1)}>
                        -
                      </button>
                      <span>{item.quantity}</span>
                      <button onClick={() => updateQuantity(item.id, item.quantity + 1)}>
                        +
                      </button>
                    </div>
                  </div>
                  <button 
                    onClick={() => removeFromCart(item.id)}
                    className="remove-button"
                  >
                    Удалить
                  </button>
                </div>
              ))}
            </div>
            
            <div className="cart-summary">
              <h3>Итого</h3>
              <div className="total-price">{cartTotal.toLocaleString()} ₽</div>
              <button 
                onClick={handleCheckout}
                className="checkout-button neon-button"
                disabled={cartItems.length === 0}
              >
                {isAuthenticated ? 'Оформить заказ' : 'Войти для оформления'}
              </button>
            </div>
          </div>
        )}
      </main>
      <Footer />
    </div>
  );
};

export default Cart;
