import React from 'react';
import '../styles/OrderSuccess.css';
import { Link, useNavigate } from 'react-router-dom';
import { FiCheckCircle } from 'react-icons/fi';

export default function OrderSuccess({ location }) {
  const navigate = useNavigate();

  // –ï—Å–ª–∏ –Ω–µ—Ç –¥–∞–Ω–Ω—ã—Ö –æ –∑–∞–∫–∞–∑–µ ‚Äî –ø–æ–∫–∞–∑—ã–≤–∞–µ–º –æ—à–∏–±–∫—É
  const order = location?.state?.order;

  if (!order) {
    return (
      <div className="order-success">
        <FiCheckCircle size={64} color="#2ecc71" />
        <h1>üö´ –ù–µ—Ç –¥–∞–Ω–Ω—ã—Ö –æ –∑–∞–∫–∞–∑–µ</h1>
        <p>–í—ã –ø–æ–ø–∞–ª–∏ —Å—é–¥–∞ –Ω–∞–ø—Ä—è–º—É—é. –û—Ñ–æ—Ä–º–∏—Ç–µ –∑–∞–∫–∞–∑, —á—Ç–æ–±—ã —É–≤–∏–¥–µ—Ç—å –ø–æ–¥—Ç–≤–µ—Ä–∂–¥–µ–Ω–∏–µ.</p>
        <button onClick={() => navigate(-1)}>–í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞–∑–∞–¥</button>
        <Link to="/">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏</Link>
      </div>
    );
  }

  return (
    <div className="order-success">
      <FiCheckCircle size={64} color="#2ecc71" />
      <h1>üéâ –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h1>
      <p>–°–ø–∞—Å–∏–±–æ –∑–∞ –ø–æ–∫—É–ø–∫—É. –ú—ã —Å–≤—è–∂–µ–º—Å—è —Å –≤–∞–º–∏ –≤ –±–ª–∏–∂–∞–π—à–µ–µ –≤—Ä–µ–º—è.</p>

      <div className="order-details">
        <p><strong>–ù–æ–º–µ—Ä –∑–∞–∫–∞–∑–∞:</strong> #{order.id}</p>
        <p><strong>–î–∞—Ç–∞:</strong> {new Date(order.date).toLocaleString()}</p>
        <p><strong>–°—Ç–∞—Ç—É—Å:</strong> –û–∂–∏–¥–∞–µ—Ç</p>
        <p><strong>–ò—Ç–æ–≥–æ:</strong> {order.total.toLocaleString()} ‚ÇΩ</p>
      </div>

      <div className="actions">
        <Link to="/profile">–ü–µ—Ä–µ–π—Ç–∏ –∫ –ø—Ä–æ—Ñ–∏–ª—é</Link>
        <Link to="/">–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏</Link>
      </div>
    </div>
  );
}
