import React from 'react';
import { Link } from 'react-router-dom';
import { FiShoppingCart, FiSearch, FiMenu, FiX, FiUser, FiSettings } from 'react-icons/fi';
import { useCart } from '../context/CartContext';
import '../styles/header.css';

const Header = () => {
  const [isMenuOpen, setIsMenuOpen] = React.useState(false);
  const { isAuthenticated, user, cartCount } = useCart();

  const toggleMenu = () => setIsMenuOpen(!isMenuOpen);
  const closeMenu = () => setIsMenuOpen(false);

  return (
    <header className="header">
      <div className="header-container">
        {/* Мобильное меню */}
        <button className="mobile-menu-btn" onClick={toggleMenu}>
          {isMenuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
        </button>

        {/* Логотип */}
        <Link to="/" className="logo" onClick={closeMenu}>
          LI-NING
        </Link>

        {/* Основное меню */}
        <nav className={`nav-menu ${isMenuOpen ? 'active' : ''}`}>
          <Link to="/products" className="nav-link" onClick={closeMenu}>Товары</Link>
          <Link to="/about" className="nav-link" onClick={closeMenu}>О нас</Link>
          
          {/* Ссылка на админ-панель (только для админов) */}
          {user?.isAdmin && (
            <Link to="/admin" className="nav-link admin-link" onClick={closeMenu}>
              <FiSettings style={{ marginRight: 5 }} />
              Админ-панель
            </Link>
          )}
        </nav>

        {/* Блок поиска и корзины */}
        <div className="header-actions">
          <div className="search-box">
            <FiSearch className="search-icon" />
            <input type="text" placeholder="Поиск..." />
          </div>
          
          <Link to="/cart" className="cart-btn" onClick={closeMenu}>
            <FiShoppingCart />
            {cartCount > 0 && <span className="cart-count">{cartCount}</span>}
          </Link>
          
          <Link to={isAuthenticated ? "/profile" : "/auth"} className="auth-btn" onClick={closeMenu}>
            <FiUser />
            {isAuthenticated ? user.name : "Войти"}
          </Link>
        </div>
      </div>
    </header>
  );
};

export default Header;
