import React from 'react';
import { useLocation, Link } from 'react-router-dom';
import NeonButton from '../components/NeonButton';
import '../styles/OrderSuccess.css';
import { FiCheckCircle } from 'react-icons/fi';

export default function OrderSuccess() {
  const { state } = useLocation();

  // –ü—Ä–æ–≤–µ—Ä—è–µ–º –Ω–∞–ª–∏—á–∏–µ –∑–∞–∫–∞–∑–∞
  const order = state?.order;

  if (!order) {
    return (
      <div className="order-success-container">
        <div className="order-success-card error">
          <h2>üö´ –ù–µ —É–¥–∞–ª–æ—Å—å –ø–æ–ª—É—á–∏—Ç—å –¥–∞–Ω–Ω—ã–µ</h2>
          <p>–ü–æ–∂–∞–ª—É–π—Å—Ç–∞, –æ—Ñ–æ—Ä–º–∏—Ç–µ –∑–∞–∫–∞–∑ –∑–∞–Ω–æ–≤–æ.</p>
          <Link to="/cart">
            <NeonButton text="–í–µ—Ä–Ω—É—Ç—å—Å—è –≤ –∫–æ—Ä–∑–∏–Ω—É" fullWidth danger />
          </Link>
        </div>
      </div>
    );
  }

  return (
    <div className="order-success-container">
      <div className="order-success-card">
        <FiCheckCircle size={80} color="#4BB543" />
        <h1>üéâ –ó–∞–∫–∞–∑ –æ—Ñ–æ—Ä–º–ª–µ–Ω!</h1>
        <p><strong>–ù–æ–º–µ—Ä:</strong> #{order.id}</p>
        <p><strong>–°—É–º–º–∞:</strong> {order.total.toLocaleString()} ‚ÇΩ</p>
        <p><strong>–°—Ç–∞—Ç—É—Å:</strong> <span className="status-badge pending">–û–∂–∏–¥–∞–µ—Ç</span></p>

        <div className="order-success-actions">
          <Link to="/profile">
            <NeonButton text="–ú–æ–∏ –∑–∞–∫–∞–∑—ã" fullWidth danger />
          </Link>
          <Link to="/">
            <NeonButton text="–ü—Ä–æ–¥–æ–ª–∂–∏—Ç—å –ø–æ–∫—É–ø–∫–∏" fullWidth />
          </Link>
        </div>
      </div>
    </div>
  );
}
