import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import Header from '../components/Header';
import Footer from '../components/Footer';
import ProductCard from '../components/ProductCard';
import '../styles/Products.css';
import '../styles/product-card.css';
import { allProducts } from '../data/products';

const Products = () => {
    const location = useLocation();
    const [activeCategory, setActiveCategory] = useState(null);
    const [activeSubcategory, setActiveSubcategory] = useState(null);
  
    useEffect(() => {
      const params = new URLSearchParams(location.search);
      const category = params.get('category');
      setActiveCategory(category || null);
      setActiveSubcategory(null);
    }, [location]);
  
    const renderProducts = () => {
      // Показываем коллекции (главная страница товаров)
      if (!activeCategory) {
        return Object.entries(allProducts.collections).map(([collection, items]) => (
          <section key={collection}>
            <h2>{collection.toUpperCase()}</h2>
            <div className="products-grid">
              {items.slice(0, 4).map(product => (
                <ProductCard key={product.id} product={product} />
              ))}
            </div>
          </section>
        ));
      }
  
      // Показываем подкатегории выбранной категории
      if (activeCategory && !activeSubcategory) {
        const categoryData = allProducts.categories[activeCategory];
        return (
          <>
            <h1>{activeCategory.toUpperCase()}</h1>
            {Object.entries(categoryData).map(([subcategory, items]) => (
              <div key={subcategory}>
                <h2 onClick={() => setActiveSubcategory(subcategory)}>
                  {subcategory.toUpperCase()}
                </h2>
                <div className="products-grid">
                  {items.slice(0, 4).map(product => (
                    <ProductCard key={product.id} product={product} />
                  ))}
                </div>
              </div>
            ))}
          </>
        );
      }
  
      // Показываем все товары подкатегории
      const products = allProducts.categories[activeCategory][activeSubcategory];
      return (
        <>
          <button onClick={() => setActiveSubcategory(null)}>
            ← Назад к {activeCategory}
          </button>
          <h2>{activeSubcategory.toUpperCase()}</h2>
          <div className="products-grid">
            {products.map(product => (
              <ProductCard key={product.id} product={product} />
            ))}
          </div>
        </>
      );
    };
  
    return (
      <div className="products-page">
        {renderProducts()}
      </div>
    );
  };
  
  export default Products;
