import React from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/AdminPanel.css';

const AdminPanel = () => {
  const { allOrders, updateOrderStatus, isAdmin } = useCart();

  if (!isAdmin) {
    return (
      <div className="app">
        <Header />
        <main className="admin-panel">
          <h1>Доступ запрещен</h1>
          <p>Требуются права администратора</p>
        </main>
        <Footer />
      </div>
    );
  }

  const statusOptions = [
    'pending',
    'processing',
    'shipped',
    'delivered',
    'cancelled'
  ];

  return (
    <div className="app">
      <Header />
      <main className="admin-panel">
        <h1>Панель администратора</h1>
        <div className="orders-list">
          {allOrders.map(order => (
            <div key={order.id} className="order-card">
              <h3>Заказ #{order.id.slice(0, 8)}</h3>
              <p>Клиент: {order.userId.slice(0, 8)}</p>
              <p>Дата: {new Date(order.date).toLocaleString()}</p>
              <p>Товаров: {order.items.length}</p>
              <p>Сумма: {order.total.toLocaleString()} ₽</p>
              
              <div className="status-control">
                <select
                  value={order.status}
                  onChange={(e) => updateOrderStatus(order.id, e.target.value)}
                >
                  {statusOptions.map(option => (
                    <option key={option} value={option}>
                      {option}
                    </option>
                  ))}
                </select>
              </div>

              <div className="order-items">
                {order.items.map(item => (
                  <div key={item.cartId} className="order-item">
                    <img src={item.image} alt={item.name} width="50" />
                    <span>{item.name}</span>
                    <span>{item.price} ₽</span>
                  </div>
                ))}
              </div>
            </div>
          ))}
        </div>
      </main>
      <Footer />
    </div>
  );
};

export default AdminPanel;
