import React, { useState, useEffect } from 'react';
import { useCart } from '../context/CartContext';
import Header from '../components/Header';
import Footer from '../components/Footer';
import '../styles/AdminPanel.css';
import { FiPackage, FiSettings, FiLogOut, FiAlertTriangle } from 'react-icons/fi';
import { useNavigate } from 'react-router-dom';
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const AdminPanel = () => {
  // First, get all the context values
  const { user, logout, isAdmin } = useCart();
  
  // Then declare other state variables
  const [allOrders, setAllOrders] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const navigate = useNavigate();

  // Status options
  const statusOptions = [
    { value: 'pending', label: 'Ожидает' },
    { value: 'processing', label: 'В обработке' },
    { value: 'shipped', label: 'Отправлен' },
    { value: 'delivered', label: 'Доставлен' },
    { value: 'cancelled', label: 'Отменен' }
  ];

  // Mock orders data
  const mockOrders = [
    {
      id: 'order-1',
      date: new Date(),
      status: 'pending',
      total: 4500,
      items: [
        { id: 'item-1', name: 'Товар 1', price: 1500, quantity: 1 },
        { id: 'item-2', name: 'Товар 2', price: 3000, quantity: 1 }
      ]
    }
  ];

  // Add the admin check in useEffect
  useEffect(() => {
    const checkAdminAccess = async () => {
      if (!isAdmin()) {
        toast.error('Доступ запрещен. Требуются права администратора');
        navigate('/profile');
        return;
      }
      
      // Load orders only if admin
      setIsLoading(true);
      setTimeout(() => {
        setAllOrders(mockOrders);
        setIsLoading(false);
      }, 500);
    };

    checkAdminAccess();
  }, [isAdmin, navigate]);

  // Rest of your component functions
  const handleLogout = () => {
    logout();
    toast.info('Вы вышли из системы', {
      position: "top-center",
      autoClose: 3000,
    });
    navigate('/');
  };

  const updateOrderStatus = async (orderId, newStatus) => {
    try {
      setAllOrders(prevOrders => 
        prevOrders.map(order => 
          order.id === orderId ? { ...order, status: newStatus } : order
        )
      );
      
      toast.success(`Статус заказа #${orderId} изменен на "${newStatus}"`, {
        position: "top-center",
        autoClose: 3000,
      });
    } catch (error) {
      toast.error('Ошибка при обновлении статуса', {
        position: "top-center",
        autoClose: 5000,
      });
    }
  };

  // Render logic
  if (!isAdmin()) {
    return (
      <div className="admin-layout">
        <Header />
        <main className="admin-panel">
          <div className="admin-denied">
            <FiAlertTriangle size={48} className="denied-icon" />
            <h1>Доступ запрещен</h1>
            <p>Требуются права администратора</p>
            <button onClick={() => navigate('/')} className="return-btn">
              Вернуться на главную
            </button>
          </div>
        </main>
        <Footer />
      </div>
    );
  }

  return (
    <div className="admin-layout">
      <Header />
      <main className="admin-panel">
        {/* ... rest of your admin panel JSX ... */}
      </main>
      <Footer />
    </div>
  );
};

export default AdminPanel;
