      updatedCart = state.cartItems.map((item) =>
        item.productId === product.id
          ? { ...item, quantity: item.quantity + 1 }
          : item
      );
    } else {
      updatedCart = [
        ...state.cartItems,
        {
          productId: product.id,
          name: product.name,
          price: product.price,
          image: product.image,
          cartId: `${product.id}_${Date.now()}`,
          quantity: 1
        }
      ];
    }

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
    toast.success('–¢–æ–≤–∞—Ä –¥–æ–±–∞–≤–ª–µ–Ω –≤ –∫–æ—Ä–∑–∏–Ω—É');
  };

  // –£–¥–∞–ª–µ–Ω–∏–µ —Ç–æ–≤–∞—Ä–∞ –∏–∑ –∫–æ—Ä–∑–∏–Ω—ã
  const removeFromCart = async (cartId) => {
    if (!state.user) return;

    const updatedCart = state.cartItems.filter(
      (item) => item.cartId !== cartId
    );

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
  };

  // –û–±–Ω–æ–≤–ª–µ–Ω–∏–µ –∫–æ–ª–∏—á–µ—Å—Ç–≤–∞ —Ç–æ–≤–∞—Ä–∞
  const updateQuantity = async (cartId, newQuantity) => {
    if (!state.user) return;

    const updatedCart = state.cartItems.map((item) =>
      item.cartId === cartId
        ? { ...item, quantity: Math.max(1, newQuantity) }
        : item
    );

    safeSetState({ cartItems: updatedCart });
    await saveCart(updatedCart);
  };

  // –†–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏—è –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
  const register = async (name, email, password) => {
    safeSetState({ isLoading: true, error: null });

    try {
      const response = await fetch(`${API_BASE_URL}/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, password })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || '–û—à–∏–±–∫–∞ —Ä–µ–≥–∏—Å—Ç—Ä–∞—Ü–∏–∏');
      }

      const userData = await response.json();

      if (!userData.user || !userData.user.email) {
        throw new Error('–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –æ—Ç–≤–µ—Ç–∞ —Å–µ—Ä–≤–µ—Ä–∞');
      }

      saveUserToStorage(userData); // –°–æ—Ö—Ä–∞–Ω—è–µ–º –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      await fetchCart(userData.user.id); // –ó–∞–≥—Ä—É–∂–∞–µ–º –∫–æ—Ä–∑–∏–Ω—É

      return userData;
    } catch (error) {
      safeSetState({ error: error.message });
      throw error;
    } finally {
      safeSetState({ isLoading: false });
    }
  };

  // –í—Ö–æ–¥ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è –∏–ª–∏ –∞–¥–º–∏–Ω–∞
  const login = async (email, password) => {
    safeSetState({ isLoading: true, error: null });

    try {
      // –ü—Ä–æ–≤–µ—Ä–∫–∞ –Ω–∞ –∞–¥–º–∏–Ω–∞
      if (
        email === ADMIN_CREDENTIALS.email &&
        password === ADMIN_CREDENTIALS.password
      ) {
        const adminUser = {
          id: 'admin-1',
          name: 'Admin',
          email: ADMIN_CREDENTIALS.email,
          isAdmin: true,
          token: 'mock-admin-token'
        };

        localStorage.setItem('user', JSON.stringify(adminUser));
        localStorage.setItem('token', adminUser.token);

        safeSetState({
          user: adminUser,
          isAuthenticated: true,
          isLoading: false
        });

        return adminUser;
      }

      // –õ–æ–≥–∏–∫–∞ –¥–ª—è –æ–±—ã—á–Ω–æ–≥–æ –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è
      const response = await fetch(`${API_BASE_URL}/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.message || '–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞');
      }

      const userData = await response.json();

      if (!userData.user || !userData.user.email) {
        throw new Error('–ù–µ–≤–µ—Ä–Ω—ã–π —Ñ–æ—Ä–º–∞—Ç –¥–∞–Ω–Ω—ã—Ö –ø–æ–ª—å–∑–æ–≤–∞—Ç–µ–ª—è');
      }

      saveUserToStorage(userData); // –°–æ—Ö—Ä–∞–Ω—è–µ–º —Å email
      await fetchCart(userData.user.id);
      fetchCurrentUser(); // –ü—Ä–∏–Ω—É–¥–∏—Ç–µ–ª—å–Ω–æ–µ –æ–±–Ω–æ–≤–ª–µ–Ω–∏–µ –ø—Ä–æ—Ñ–∏–ª—è

      return userData;
    } catch (error) {
      safeSetState({ error: error.message });
      console.error('–û—à–∏–±–∫–∞ –≤—Ö–æ–¥–∞:', error);
      throw error;
    } finally {
      safeSetState({ isLoading: false });
    }
  };

  // –í—ã—Ö–æ–¥ –∏–∑ –∞–∫–∫–∞—É–Ω—Ç–∞
  const logout = () => {
    localStorage.removeItem('user');
    localStorage.removeItem('token');

    safeSetState({
      user: null,
      isAuthenticated: false,
      cartItems: []
    });
  };

  return (
    <CartContext.Provider
      value={{
        cartItems: state.cartItems,
        cartCount: state.cartItems.reduce((sum, item) => sum + item.quantity, 0),
        addToCart,
        removeFromCart,
        updateQuantity,
        user: state.user,
        isAuthenticated: state.isAuthenticated,
        isLoading: state.isLoading,
        error: state.error,
        login,
        logout,
        register,
        isAdmin: () => state.user?.isAdmin === true,
        fetchCurrentUser
      }}
    >
      {children}
    </CartContext.Provider>
  );
}

export const useCart = () => {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart –¥–æ–ª–∂–µ–Ω –∏—Å–ø–æ–ª—å–∑–æ–≤–∞—Ç—å—Å—è –≤–Ω—É—Ç—Ä–∏ CartProvider');
  }
  return context;
};
