import React, { useState } from 'react';
import { FiMenu, FiShoppingCart, FiSearch, FiX } from 'react-icons/fi';
import '../styles/main.css';
import '../styles/header.css';

export default function Header() {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <header className="header">
      <div className="header-container container">
        <button 
          className="mobile-menu-btn"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? <FiX size={24} /> : <FiMenu size={24} />}
        </button>

        <div className="logo">LI-NING</div>
        
        <nav className={`nav-menu ${menuOpen ? 'active' : ''}`}>
          <a href="/shop" className="nav-link">Товары</a>
          <a href="/tech" className="nav-link">Технологии</a>
          <a href="/login" className="nav-link">Вход</a>
        </nav>

        <div className="search-cart-wrapper">
          <button className="cart-button">
            <FiShoppingCart size={20} />
          </button>
          <div className="search-container">
            <input
              type="text"
              placeholder="Поиск..."
              className="search-input"
            />
            <FiSearch className="search-icon" />
          </div>
        </div>
      </div>
    </header>
  );
}
